mapper.insert:
INSERT INTO REVIEW
VALUES(SEQ_REVIEW_NO.NEXTVAL
	, #{itemNo}
	, #{reviewContent}
	, #{reviewRate}
	, #{reviewImg1}
	, #{reviewImg2}
	, SYSDATE
	, DEFAULT
	, #{memberNo}
	, #{memberId}
	, #{email})

mapper.update:
UPDATE ITEM
SET ITEM_RATE=(SELECT TRUNC(AVG(REVIEW_RATE),1) FROM REVIEW)
WHERE ITEM_NO=#{itemNo}
